import { request, response } from "express";
import { ComponentTree } from "../params/ComponentTree";
import { initTree } from './util';
export function Controller(options: any = {}) {
  
  return function (constructor: Function) {
    constructor.prototype.request = request;
    constructor.prototype.response = response;
    let constructor_name = constructor.name.toString().toLowerCase()
    initTree(constructor_name)
    if(options.hasOwnProperty("url")){
      ComponentTree.components[constructor_name].base_url= options.url
    }
    ComponentTree.components[constructor_name].constructor = constructor
    
  };

 
}

import { ComponentTree } from './../params/ComponentTree';
export function initTree(class_name : string, method_name : string = "") {
    if (method_name === undefined) { method_name = ""; }
    if (ComponentTree.components === undefined)
        ComponentTree.components = {};
    if (!ComponentTree.components.hasOwnProperty(class_name)) {
        ComponentTree.components[class_name] = {
            base_url: null,
            methods: []
        };
    }
    if (method_name == "" || method_name == undefined)
        return;
    if (ComponentTree.components[class_name].methods[method_name] == undefined)
        ComponentTree.components[class_name].methods[method_name] = {};
}

import { ComponentTree } from '../params/ComponentTree';
import { RequestOptions } from './requestmapping.options';
import { Methods } from "./requestmethod.decorator";
import { initTree } from "./util";
export function RequestMapping(options : RequestOptions) {
  if(!options.hasOwnProperty("url"))
      throw new Error("Property URL must be present")
  if(!options.hasOwnProperty("method"))
      throw new Error("Property method must be present")
      
  let url = options.url
  let method = options.method
  if(!Object.values(Methods).includes(method))
      throw new Error("Property method must be of type Method")

  return (target: any, propertyKey: string, descriptor: PropertyDescriptor) => {
    
    let class_name : string = target.constructor.name.toLowerCase()  
    let method_name : string = propertyKey.toLowerCase()
    initTree(class_name, method_name)
    ComponentTree.components[class_name].methods[method_name].url = url.replace(/\/$/, "");
    ComponentTree.components[class_name].methods[method_name].action = target[propertyKey]
    ComponentTree.components[class_name].methods[method_name].parameter_count = getParamNames(target[propertyKey]).length
    ComponentTree.components[class_name].methods[method_name].method = method
    ComponentTree.components[class_name].methods[method_name].name = propertyKey


    if(options.authenticated!==undefined){
      ComponentTree.components[class_name].methods[method_name].authenticated = options.authenticated
    }

  };
}

var STRIP_COMMENTS = /((\/\/.*$)|(\/\*[\s\S]*?\*\/))/gm;
var ARGUMENT_NAMES = /([^\s,]+)/g;
function getParamNames(func: Function) {
  var fnStr = func.toString().replace(STRIP_COMMENTS, "");
  var result = fnStr
    .slice(fnStr.indexOf("(") + 1, fnStr.indexOf(")"))
    .match(ARGUMENT_NAMES);
  if (result === null) result = [];
  return result;
}

import { ComponentTree } from './../params/ComponentTree';
import { initTree } from './util';
export function RequestParam(paramName : string){
    return function(target : any, propertyKey : string | symbol, parameterIndex : number){
        let class_name = target.constructor.name.toLowerCase()
        let method_name = propertyKey.toString().toLowerCase()
        initTree(class_name, method_name)
        if(!ComponentTree.components[class_name].methods[method_name].hasOwnProperty("params"))
            ComponentTree.components[class_name].methods[method_name].params = []
            
        ComponentTree.components[class_name].methods[method_name].params.push({
            "name" : paramName,
            "index" : parameterIndex
        })

    }
}

import { ComponentTree } from "../params/ComponentTree"
import { initTree } from "./util"

export function Authenticated(){
    return (target : any, propertyKey : string, descriptor : PropertyDescriptor) =>{
        let class_name : string = target.constructor.name.toLowerCase()  
        let method_name : string = propertyKey.toLowerCase()
        initTree(class_name, method_name)
        ComponentTree.components[class_name].methods[method_name].authenticated = true

    }
}

import { ResponseType } from './requestmethod.decorator';
import { ComponentTree } from "../params/ComponentTree";
import { initTree } from "./util";

export function ResponseBody() {
   
  
    return (target: any, propertyKey: string, descriptor: PropertyDescriptor) => {
      
      let class_name : string = target.constructor.name.toLowerCase()  
      let method_name : string = propertyKey.toLowerCase()
      initTree(class_name, method_name)
      ComponentTree.components[class_name].methods[method_name].response_type = ResponseType.JSON
  
    };
  }

